volatile unsigned int idx = 0;
volatile unsigned int edge = 0;


volatile unsigned char shuttersOn = 0;
volatile unsigned int distressMode = 0x01;

void shutterOn(void);
void shutterOff(void);

int main(void){
    WDTCTL = WDTPW + WDTHOLD;           // Stop watchdog timer
    BCSCTL3 = LFXT1S_2; //select VLO as ACLK

    //init all pins for pwr opt
    P1DIR = 0xF8; //All output except P1[1,2]
    P2DIR = 0xFF;

    TA0CTL |= TASSEL0;  //use ACLK for TimerA0
    TA1CTL |= TASSEL0;  //use ACLK for TimerA1

    TA1CTL &= ~MC0;      // count up mode (off)

    //timer A0 sets the distress frequency
    //on TA0CCR0 interupt it enagble/disable gpio after toggling flag
    distressMode = 0b111 & ~P1IN; //read P1[0-2] for user input
    if(distressMode== 0b001){
        TA0CCR0 = DELAY1;
    } else if(distressMode==0b010){
        TA0CCR0 = DELAY2;
    } else if(distressMode==0b100){
        TA0CCR0 = DELAY3;
    } else {
       TA0CCR0 = DELAY1;
    }

    //timer A1 directly drives the shutters. Resets every SWITCH_PERIOD/2 cycles
    //each CCR is in differnet mode
    TA1CCR0 = SWITCH_PERIOD; // set shutter driver freq
    TA1CCR1 = SWITCH_PERIOD/2; // duty cyle 50%
    TA1CCR2 = SWITCH_PERIOD/2; // duty cyle 50%
    TA1CCTL1 |= (OUTMOD0 + OUTMOD1); // output reset/set mode
    TA1CCTL2 |= (OUTMOD1 + OUTMOD2); // output set/reset mode (antiphase)

    P2SEL |= (BIT2+BIT4); //attache P2[2,4] to TA1CCR1

    P1IES |= (BIT0 + BIT1 + BIT2); //falling edge interrupts
    P1IE |= (BIT0 + BIT1 + BIT2); // enable P1.[0-2] interupts

    P1DIR |= BIT6;
    P1OUT &=~ BIT6; // turn LED2 off

    TA0CCTL0 |= CCIE; // enable timerA compare 0 interrupt
    TA0CTL |= MC0;      //start A0 count up mode
    _BIS_SR(LPM3_bits+GIE); //enter low power mode 3 and enable interupts
}



